<!doctype html><html lang='en'><head>    <meta charset='utf-8'>    <meta name='viewport' content='width=device-width, initial-scale=1'>    <link rel='stylesheet' href='https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css'>    <link rel='stylesheet' href='{{asset('css/main.css')}}'>    <link rel="stylesheet" type="text/css" href="fonts/style.css"/>    <!--    jQuery-->    <script src='http://code.jquery.com/jquery-1.12.4.min.js'></script>    <script src='https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js'></script>    <!-- Buttons -->    <link href="{{asset('css/app.css')}}" rel="stylesheet" type="text/css">    <title>Sked</title>    <meta http-equiv="Expires" content="0">    <meta http-equiv="Last-Modified" content="0">    <meta http-equiv="Cache-Control" content="no-cache, mustrevalidate">    <meta http-equiv="Pragma" content="no-cache"></head><body type="hidden"><div  id="main-container" class='container-fluid'>    <div class='row'>        <div class="col-xs-12">            <a href="/"><h1 class="logo-text">Sked</h1></a>        </div>    </div>    <div class='row'>        <div class="col-xs-12">            <p class="slogan-text">Thanks for sked!</p>        </div>    </div>    <div class="get-premium col-xs-12">        <form method="get" action="/premium">            <button type="submit" class="btn btn-primary outline pull-right">                Get Premium</button>        </form>    </div>    <input type="hidden" id="input-email" value="{{session('email')}}">    <div  class="row">        <div class="col-sm-6 col-sm-offset-3 col-xs-12" style="padding-top: 40px">            <div class="text">Help us improve Sked, leave a comment:</div>            <textarea id='input-comment' class="form-control" rows="9"></textarea>        </div>    </div>    <div class="row">        <div class="col-sm-4 col-sm-offset-4 col-xs-12">            <button type="button" id="btn-done"                    class="btn text btn-primary outline">Comment</button>        </div>    </div>    <div class="col-xs-12" style="height:100px;"></div></div></body><script>    $('#btn-done').click(function() {        var email = $('#input-email').val();        var comment = $('#input-comment').val();        var data = {            'email': email,            'comment': comment        };        $.ajax({            type: "POST",            url: '/comment',            data: data,            headers: {                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')            },            success: function () {                $.loadingBlockHide;                window.location.href = '/comment';            }        });    });</script></html>